/**
 * Serialises data into a Buffer.
 *
 * Maps are converted into objects before serialisation.
 * Dates are converted into ISO strings before serialisation.
 * @param payload
 */
export declare function toBuffer(payload: any): Buffer;
/**
 * Deserialises data from a Buffer
 * @param buffer
 */
export declare function fromBuffer(buffer: Buffer): any;
/**
 * Deserialises data from an ArrayBuffer
 * @param buffer
 */
export declare function fromBuffer(buffer: ArrayBuffer): any;
