import { Context } from 'fabric-contract-api';
import PathTree from './pathTree';
/**
 * RuleBook is a wrapper class for SetTree that uses it to store a
 * Kraken permission tree.
 */
export default class RuleBook {
    private pathTree;
    constructor(initialTree?: PathTree);
    /**
     * Retrieves a rule at the given path.
     * @param path
     */
    getRule(path: string[]): (ctx: Context) => boolean;
    /**
     * Creates a rule at the given path, cannot overwrite existing rules.
     * @param path
     * @param rule
     */
    createRule(path: string[], rule?: (ctx: Context) => boolean): void;
    /**
     * Updates an existing rule at the given path.
     * @param path
     * @param newRule
     * @param replace
     */
    updateRule(path: string[], newRule: (ctx: Context) => boolean, replace?: boolean): void;
    /**
     * Removes an existing rule at the given path.
     * @param path
     */
    deleteRule(path: string[]): void;
}
