/// <reference types="node" />
/**
 * A 'string' to 'any' mapping of a KV pair in state
 */
export interface MockStatePair {
    key: string | string[];
    value?: any;
}
/**
 * Contains a MockState and provides extra helper functions.
 */
export default class MockState extends Map<string, Buffer> {
    /**
     * Returns the value at the given key deserialised from buffer form. Returns 'undefined' if key does not exist.
     * @param key
     */
    getDeserialised(key: string | string[]): any;
    deserialised(returnType: MapConstructor): Map<string, any>;
    deserialised(): MockStatePair[];
}
